policy:
path "ssh-client-onetime-pass/*" {
  capabilities = [ "list" ]
}

path "ssh-client-onetime-pass/creds/*" {
  capabilities = ["create", "read", "update"]
}

path "ssh-client-onetime-pass/roles/*" {
  capabilities = ["create", "read", "update"]
}

path "tempusers/data/linux/*" {
  capabilities = ["list", "read","update","create"]
}
